import produce from "immer";

const INITIAL_STATE = {
  route: {
    name: "Home",
    path: "/",
  },
};

export default function route(state = INITIAL_STATE, action) {
  return produce(state, (draft) => {
    switch (action.type) {
      case "@route/SET_ROUTE":
        const { name, path } = action.payload.route;
        draft.route.name = name;
        draft.route.path = path;
        break;

      default:
    }
  });
}
