import React from "react";
import PropTypes from "prop-types";

import Header from "../../components/Header";
import MobileForm from "./components/MobileForm";

import { Container, Menu, Nav, Name } from "./styles";

function View({ selected, setSelected, width }) {
  const tabs = ["Cartão de crédito", "Boleto"];

  function renderTabs(tab) {
    if (tab === selected)
      return (
        <Nav>
          <Name selected onClick={() => setSelected(tab)}>
            {tab}
          </Name>
        </Nav>
      );

    return (
      <Nav>
        <Name onClick={() => setSelected(tab)}>{tab}</Name>
      </Nav>
    );
  }

  return (
    <Container>
      <Header />
      <Menu>{tabs.map((tab) => renderTabs(tab))}</Menu>
      <MobileForm width={width} />
    </Container>
  );
}

View.propTypes = {
  selected: PropTypes.string,
  setSelected: PropTypes.func,
  width: PropTypes.number,
};

View.defaultProps = {
  selected: "",
  setSelected: () => {},
  width: window.innerWidth,
};

export default View;
