import React from "react";
import PropTypes from "prop-types";

import { Redirect } from "react-router-dom";

import {
  Container,
  Column,
  Form,
  Photo,
  File,
  Input,
  Button,
  Cloud,
  Title,
  Card,
  ProductPhoto,
  Description,
  Request,
  WrapperQuantity,
  Quantity,
  SmallLabel,
  Info,
  Date,
  Total,
  List,
  Scroll,
  Logout,
} from "./styles";

function DesktopForm({
  openInput,
  onSelectFile,
  username,
  setUsername,
  name,
  setName,
  password,
  setPassword,
  email,
  setEmail,
  logout,
  signed,
  update,
}) {
  const requests = [1, 2, 3, 4, 5];

  function renderRequest() {
    return (
      <Card>
        <ProductPhoto />
        <Description>
          <Request>Pedido #23</Request>
          <WrapperQuantity>
            <Quantity>4</Quantity>
            <SmallLabel>items</SmallLabel>
          </WrapperQuantity>
        </Description>
        <Info>
          <Date>02/10/1997</Date>
          <Total>R$ 260,00</Total>
        </Info>
      </Card>
    );
  }

  return (
    <Container>
      <Column>
        <Form>
          <Photo onClick={openInput} />
          <File
            type="file"
            id="file"
            accept="image/png, image/jpeg"
            onChange={onSelectFile}
          />
          <Input
            placeholder="nome do usuário"
            value={name}
            onChange={(event) => setName(event.target.value)}
            marginTop={50}
          />
          <Input
            placeholder="usuário"
            value={username}
            onChange={(event) => setUsername(event.target.value)}
            marginTop={50}
          />
          <Input
            placeholder="e-mail"
            value={email}
            onChange={(event) => setEmail(event.target.value)}
            marginTop={50}
          />
          <Input
            placeholder="senha"
            value={password}
            onChange={(event) => setPassword(event.target.value)}
            marginTop={50}
          />
          <Button
            onClick={(event) => {
              event.preventDefault();
              update();
            }}
          >
            <Cloud />
          </Button>
          <Logout onClick={logout}>Sair</Logout>
        </Form>
      </Column>
      <Column justify="flex-start">
        <Title>Últimos pedidos</Title>
        <List>
          {" "}
          <Scroll>{requests.map((request) => renderRequest())}</Scroll>
        </List>
      </Column>
      {!signed && <Redirect to="/" />}
    </Container>
  );
}

DesktopForm.propTypes = {
  openInput: PropTypes.func,
  onSelectFile: PropTypes.func,
  logout: PropTypes.func,
  signed: PropTypes.bool,
  update: PropTypes.func,
  username: PropTypes.string,
  setUsername: PropTypes.func,
  name: PropTypes.string,
  setName: PropTypes.func,
  email: PropTypes.string,
  setEmail: PropTypes.func,
  password: PropTypes.string,
  setPassword: PropTypes.func,
};

DesktopForm.defaultProps = {
  openInput: () => {},
  onSelectFile: () => {},
  logout: () => {},
  signed: false,
  update: () => {},
  username: "",
  setUsername: () => {},
  name: "",
  setName: () => {},
  email: "",
  setEmail: () => {},
  password: "",
  setPassword: () => {},
};

export default DesktopForm;
