import styled from "styled-components";
import { FiUploadCloud } from "react-icons/fi";

export const Container = styled.div`
  height: ${window.innerHeight}px;

  @media (min-width: 1024px) {
    display: flex;
    align-items: center;
    justify-content: center;
  }
`;

export const Column = styled.div`
  display: flex;
  align-items: center;
  justify-content: flex-start;
  flex-direction: column;
  min-height: 550px;
`;

export const File = styled.input.attrs({
  type: "file",
})`
  display: none;
`;

export const Form = styled.div`
  width: 450px;
  height: 550px;
  position: relative;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
  background: #eee;
  border-radius: 20px;
  margin-right: 50px;
`;

export const Photo = styled.img`
  width: 150px;
  height: 150px;
  border-radius: 50%;
  background: #c5c5c5;
  position: absolute;
  top: -75px;
`;

export const Input = styled.input`
  width: 300px;
  height: 45px;
  color: #000;
  text-align: center;
  font-size: 24px;
  margin-top: ${(props) => (props.marginTop ? props.marginTop : 0)}px;

  &::placeholder {
    color: #000;
  }
`;

export const Button = styled.button`
  width: 200px;
  height: 55px;
  display: flex;
  align-items: center;
  justify-content: center;
  position: absolute;
  bottom: -22.5px;
  background: #424242;
  border-radius: 10px;
`;

export const Cloud = styled(FiUploadCloud)`
  width: 32px;
  height: 32px;
  color: #fff;
`;

export const Title = styled.h2`
  font-weight: unset;
  font-size: 24px;
`;
