import React, { useState } from "react";
import PropTypes from "prop-types";

import Content from "../Content";
import {
  Container,
  Search,
  Icon,
  Input,
  WrapperMenu,
  Menu,
  Label,
} from "./styles";

function List() {
  const [nav, setNav] = useState("Todos");
  const navs = [
    "Todos",
    "Camisas",
    "Blusas",
    "Bones",
    "Casacos",
    "Meia",
    "Shorts",
    "Copos",
    "Canecas",
    "Tirantes",
  ];

  // const products = useSelector((state) => state.products.products);
  // const categories = useSelector((state) => state.products.categories);

  function onClick(event) {
    setNav(event.target.innerHTML);
  }

  function renderLabel(name) {
    const selected = navs.find((item) => item === nav);

    if (name === selected)
      return (
        <Label onClick={onClick} selected>
          {name}
        </Label>
      );

    return <Label onClick={onClick}>{name}</Label>;
  }

  return (
    <Container>
      <Content>
        <Search>
          <Input placeholder="Filtrar" />
          <Icon />
        </Search>
        <WrapperMenu>
          <Menu>{navs.map((name) => renderLabel(name))}</Menu>
        </WrapperMenu>
      </Content>
    </Container>
  );
}

List.propTypes = {
  data: PropTypes.array,
};

List.defaultProps = {
  data: [],
};

export default List;
