import React from "react";
import PropTypes from "prop-types";

import {
  Card,
  WrapperInput,
  SmallLabel,
  Row,
  Input,
  InputCVV,
  InputCard,
  InputDate,
  Button,
  CreditCard,
  Back,
  Baseline,
  Quantity,
  Total,
  Dolar,
  Footer,
} from "./styles";

function MobileForm({ selected }) {
  function renderCreditCard() {
    return (
      <>
        <Card />
        <WrapperInput>
          <SmallLabel>número do cartão</SmallLabel>
          <InputCard placeholder="0000 0000 0000 0000" />
        </WrapperInput>
        <Row>
          <WrapperInput>
            <SmallLabel>CVV</SmallLabel>
            <InputCVV placeholder="000" />
          </WrapperInput>
          <WrapperInput>
            <SmallLabel>expiração</SmallLabel>
            <InputDate placeholder="00/0000" />
          </WrapperInput>
        </Row>
        <WrapperInput>
          <SmallLabel>nome do proprietário</SmallLabel>
          <Input placeholder="nome do proprietário" />
        </WrapperInput>
        <Button>
          <CreditCard />
        </Button>
        <Back>voltar</Back>
      </>
    );
  }

  function renderBillet() {
    return (
      <>
        <Baseline marginTop={100}>
          <Quantity>4</Quantity>
          <SmallLabel>items</SmallLabel>
        </Baseline>
        <Baseline marginTop={30} marginBottom={100}>
          <Total>R$ 260,00</Total>
        </Baseline>
        <Footer>
          <Button>
            <Dolar />
          </Button>
          <Back>voltar</Back>
        </Footer>
      </>
    );
  }

  return <>{selected === "Boleto" ? renderBillet() : renderCreditCard()}</>;
}

MobileForm.propTypes = {
  selected: PropTypes.string,
};

MobileForm.defaultProps = {
  selected: "Boleto",
};

export default MobileForm;
