import { all, takeLatest, call, race, put } from "redux-saga/effects";
import {
  indexProductCategoriesSuccess,
  productCategoriesFailure,
} from "./actions";
import api from "~/services/api";
import timer from "../utils/timer";
import errorHandler from "../utils/errorHandler";

function* productCategories({ payload }) {
  try {
    const { id } = payload.params;

    const { response } = yield race({
      response: call(api.get, "/assign/category"),
      timeout: call(timer),
    });

    yield put(indexProductCategoriesSuccess(response.data));
  } catch (error) {
    yield errorHandler(error, productCategoriesFailure);
  }
}

export default all([
  takeLatest(
    "@productCategories/INDEX_PRODUCT_CATEGORIES_REQUEST",
    productCategories
  ),
]);
