import produce from "immer";

const INITIAL_STATE = {
  categories: [],
  status: {
    loading: false,
  },
  error: {
    state: false,
    message: "",
    reasons: [],
  },
};

export default function productCategories(state = INITIAL_STATE, action) {
  return produce(state, (draft) => {
    switch (action.type) {
      case "@productCategories/INDEX_PRODUCT_CATEGORIES_REQUEST":
        draft.status.loading = true;
        draft.error = INITIAL_STATE.error;
        break;

      case "@productCategories/INDEX_PRODUCT_CATEGORIES_SUCCESS":
        draft.status.loading = false;
        draft.categories = action.payload.body;
        break;

      case "@productCategories/CATEGORIES_FAILURE":
        const { message, reasons } = action.payload;
        draft.status.loading = false;
        draft.error.status = true;
        draft.error.message = message;
        draft.error.reasons = reasons;
        break;

      default:
    }
  });
}
