import produce from "immer";
import _ from "lodash";

const INITIAL_STATE = {
  sells: [],
  page: 0,
  status: {
    loading: false,
  },
  error: {
    status: false,
    message: "",
    reasons: [],
  },
};

export default function sells(state = INITIAL_STATE, action) {
  return produce(state, (draft) => {
    switch (action.type) {
      case "@sell/INDEX_SELL_REQUEST":
        draft.status.loading = true;
        draft.error = INITIAL_STATE.error;
        break;

      case "@sell/INDEX_SELL_SUCCESS":
        // const unionIndex = _.unionBy(state.sells, action.payload.body, "id");
        // draft.sells = _.orderBy(
        //   unionIndex,
        //   ["created_at", "payament_id_fk", "name"],
        //   ["asc", "asc", "asc"]
        // );
        draft.status.loading = false;
        draft.page = state.page + 1;
        break;

      case "@sell/SELL_FAILURE":
        const { message, reasons } = action.payload;
        draft.status.loading = false;
        draft.error.status = true;
        draft.error.message = message;
        draft.error.reasons = reasons;
        break;

      case "@sell/RESET_PAGE":
        draft.page = 0;
        break;

      case "@sell/RESET_SELL":
        draft.sells = [];
        break;

      default:
    }
  });
}
