import React, { useState, useEffect } from "react";

import {
  FacebookShareButton,
  EmailShareButton,
  WhatsappShareButton,
} from "react-share";
import {
  addToCart,
  removeFromCart,
  removeOneFromCart,
} from "../../store/modules/cart/actions.js";
import { setRoute } from "../../store/modules/route/actions";

import { useDispatch, useSelector } from "react-redux";

import asset1 from "../../assets/images/items/asset1.png";

import Header from "../../components/Header";

import {
  Container,
  Name,
  Description,
  Controller,
  Minus,
  Count,
  Plus,
  Trash,
  Board,
  Photo,
  Price,
  Total,
  Label,
  Value,
  Facebook,
  Whatsapp,
  Email,
  Social,
  Tags,
  Tag,
  Content,
  Info,
} from "./styles";

function Product({ match: { params } }) {
  const [product, setProduct] = useState(null);

  const products = useSelector((state) => state.products.products);
  const totalCart = useSelector((state) => state.cart.total);
  const productsCart = useSelector((state) => state.cart.products);
  const dispatch = useDispatch();

  // const productMock = {
  //   id: 1,
  //   name: "Caneca JOIA 2018",
  //   image: asset1,
  //   imageSmall: true,
  //   stock: 5,
  //   amount: 0,
  //   category: "Canecas",
  //   description: "Copo preto metálico do JOIA 2018 versão kit.",
  //   price: 30.0,
  //   url:
  //     "https://www.facebook.com/atletica.uff/photos/pcb.1220790521600500/1220790488267170/",
  // };

  useEffect(() => {
    const { id } = params;
    const finded = products.find((one) => one.id === id);
    setProduct(finded);
    window.scrollTo(0, 0);

    dispatch(setRoute("Product"));
  }, []);

  const price = product.price.toLocaleString("pt-BR", {
    style: "currency",
    currency: "BRL",
  });

  const total = totalCart.toLocaleString("pt-BR", {
    style: "currency",
    currency: "BRL",
  });

  let count = 0;

  productsCart.forEach((product) => {
    if (product.id === product.id) count = product.amount;
  });

  return (
    <>
      <Header />
      <Container>
        <Content>
          <Board>
            <Photo imageSmall={true} src={product.image} />
            <Price>{price}</Price>
          </Board>
          <Info>
            <Name>{product.name}</Name>
            <Tags>
              <Label>tags:</Label>
              <Tag>{product.category.map((category) => category.category)}</Tag>
            </Tags>
            <Description>{product.description}</Description>
            <Controller>
              <Minus onClick={() => dispatch(removeOneFromCart(product))} />
              <Count>{count}</Count>
              <Plus onClick={() => dispatch(addToCart(product))} />
              <Trash onClick={() => dispatch(removeFromCart(product))} />
            </Controller>
            <Total>
              <Label>total</Label>
              <Value>{total}</Value>
            </Total>
          </Info>
          <Social>
            <FacebookShareButton url={product.image}>
              <Facebook />
            </FacebookShareButton>
            <EmailShareButton url={product.image}>
              <Email />
            </EmailShareButton>
            <WhatsappShareButton url={product.image}>
              <Whatsapp />
            </WhatsappShareButton>
          </Social>
        </Content>
      </Container>
    </>
  );
}

export default Product;
