import React from "react";
import PropTypes from "prop-types";

import { useSelector } from "react-redux";

import Content from "../Content";

import {
  Container,
  Logo,
  Wrapper,
  Nav,
  Button,
  Cart,
  User,
  Label,
  Badge,
} from "./styles";

function Header({ width }) {
  const badge = useSelector((state) => state.cart.products).length;

  return (
    <Container>
      <Content>
        <Wrapper>
          <Logo />
          <Nav>
            {badge > 0 && <Badge>{badge}</Badge>}
            <Button>
              <Cart />
              <Label>carrinho</Label>
            </Button>
            <Button>
              <User />
              <Label>login</Label>
            </Button>
          </Nav>
        </Wrapper>
      </Content>
    </Container>
  );
}

Header.propTypes = {
  width: PropTypes.number,
};

Header.defaultProps = {
  width: window.clientWidth,
};

export default Header;
