import React from "react";
import PropTypes from "prop-types";

import Header from "../../components/Header";
import MobileForm from "./components/MobileForm";
import DesktopForm from "./components/DesktopForm";

import { Container, Menu, Nav, Name } from "./styles";

function View({ selected, setSelected, width, rotate, onGrantLostFocus }) {
  const tabs = ["Cartão de crédito", "Boleto"];

  function renderTabs(tab) {
    if (tab === selected)
      return (
        <Nav>
          <Name selected onClick={() => setSelected(tab)}>
            {tab}
          </Name>
        </Nav>
      );

    return (
      <Nav>
        <Name onClick={() => setSelected(tab)}>{tab}</Name>
      </Nav>
    );
  }

  return (
    <Container>
      <Header />
      <Menu>{tabs.map((tab) => renderTabs(tab))}</Menu>
      {width < 1024 ? (
        <MobileForm selected={selected} />
      ) : (
        <DesktopForm rotate={rotate} onGrantLostfocus={onGrantLostFocus} />
      )}
    </Container>
  );
}

View.propTypes = {
  selected: PropTypes.string,
  setSelected: PropTypes.func,
  width: PropTypes.number,
  rotate: PropTypes.bool,
  cvv: PropTypes.string,
  onGrantLostFocus: PropTypes.func,
};

View.defaultProps = {
  selected: "",
  setSelected: () => {},
  width: window.innerWidth,
  rotate: false,
  cvv: "",
  onGrantLostFocus: () => {},
};

export default View;
