import styled from "styled-components";
import { FiSearch, FiTrash, FiPlus, FiMinus } from "react-icons/fi";
import PerfectScrollbar from "react-perfect-scrollbar";

export const Container = styled.div`
  width: 100%;
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const Search = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  display: flex;
  align-items: center;
  justify-content: center;
  border-bottom: 2px solid #000;
`;

export const Icon = styled(FiSearch)`
  width: 32px;
  height: 32px;
  color: #000;
`;

export const Input = styled.input`
  width: 400px;
  height: 45px;
  text-align: right;
  padding-right: 10px;
  font-size: 24px;
`;

export const WrapperMenu = styled.div`
  height: 50px;
  width: 1000px;
  position: absolute;
  top: 100px;
  margin-bottom: 50px;
`;

export const Menu = styled(PerfectScrollbar)`
  display: flex;
  align-items: center;
  justify-content: flex-start;
  flex-direction: row;
`;

export const Label = styled.p`
  font-size: 24px;
  color: ${(props) => (props.selected ? "#FFBB29" : "#000")};
  font-weight: ${(props) => (props.selected ? 800 : 400)};
  cursor: pointer;

  & + & {
    margin-left: 50px;
  }
`;

export const WrapperList = styled.section`
  flex-wrap: wrap;
  display: row;
  display: flex;
  position: absolute;
  top: 200px;
  justify-content: space-between;
`;

export const Item = styled.div`
  width: 400px;
  height: 500px;
  margin-bottom: 50px;
  position: relative;
  display: flex;
  justify-content: center;
  cursor: pointer;
`;

export const Board = styled.div`
  width: 280px;
  height: 230px;
  border-radius: 20px;
  background: #ffd271;
  position: absolute;
  bottom: 100px;
`;

export const Photo = styled.img`
  height: ${(props) => (props.imageSmall ? 300 : 450)}px;
  object-fit: contain;
  position: absolute;
  right: -100px;
  bottom: 0px;
`;

export const Controller = styled.div`
  height: 50px;
  display: flex;
  align-items: center;
  justify-content: center;
  margin-bottom: 25px;
  margin-right: 25px;
  position: absolute;
  right: 0;
  bottom: 0;
`;

export const Trash = styled(FiTrash)`
  width: 32px;
  height: 32px;
  margin-left: 100px;
  cursor: pointer;
`;

export const Plus = styled(FiPlus)`
  width: 32px;
  height: 32px;
  margin-left: 20px;
  cursor: pointer;
`;

export const Minus = styled(FiMinus)`
  width: 32px;
  height: 32px;
  margin-right: 20px;
  cursor: pointer;
`;

export const Count = styled.p`
  padding: 5px 15px;
  border-radius: 10px;
  font-size: 32px;
  color: #000;
  background: #eee;
`;
