import React from "react";
import PropTypes from "prop-types";

import {
  Card,
  WrapperInput,
  SmallLabel,
  Row,
  Input,
  InputCVV,
  InputCard,
  InputDate,
  Button,
  CreditCard,
  Back,
  Baseline,
  Quantity,
  Total,
  Dolar,
  Centralize,
  Footer,
} from "./styles";

function MobileForm({ selected }) {
  function renderCreditCard() {
    return (
      <>
        <Card />
        <WrapperInput>
          <SmallLabel>número do cartão</SmallLabel>
          <InputCard placeholder="0000 0000 0000 0000" />
        </WrapperInput>
        <Row>
          <WrapperInput>
            <SmallLabel>CVV</SmallLabel>
            <InputCVV placeholder="000" />
          </WrapperInput>
          <WrapperInput>
            <SmallLabel>expiração</SmallLabel>
            <InputDate placeholder="00/0000" />
          </WrapperInput>
        </Row>
        <WrapperInput>
          <SmallLabel>nome do proprietário</SmallLabel>
          <Input placeholder="nome do proprietário" />
        </WrapperInput>
        <Button>
          <CreditCard />
        </Button>
        <Back>voltar</Back>
      </>
    );
  }

  function renderBillet() {
    return (
      <>
        <Centralize>
          <Baseline>
            <Quantity>4</Quantity>
            <SmallLabel>items</SmallLabel>
          </Baseline>
          <Baseline marginTop={30}>
            <Total>R$ 260,00</Total>
          </Baseline>
        </Centralize>
        <Footer>
          <Button>
            <Dolar />
          </Button>
          <Back>voltar</Back>
        </Footer>
      </>
    );
  }

  return <>{selected === "Boleto" ? renderBillet() : renderCreditCard()}</>;
}

rotate = { rotate };
cardNumber = { cardNumber };
setCardNumber = { setCardNumber };
cvv = { cvv };
setCvv = { setCvv };
valid = { valid };
setValid = { setValid };
name = { name };
setName = { setName };
onGrantLostFocus = { onGrantLostFocus };

MobileForm.propTypes = {
  rotate: PropTypes.bool,
  cardNumber: PropTypes.string,
  setCardNumber: PropTypes.func,
  cvv: PropTypes.string,
  setCvv: PropTypes.func,
  valid: PropTypes.string,
  setValid: PropTypes.func,
  name: PropTypes.string,
  setName: PropTypes.func,
  selected: PropTypes.string,
  onGrantLostFocus: PropTypes.func,
};

MobileForm.defaultProps = {
  rotate: false,
  cardNumber: "0000 0000 0000 0000",
  setCardNumber: () => {},
  cvv: "000",
  setCvv: () => {},
  valid: "00/0000",
  name: "nome do proprietário",
  setName: () => {},
  selected: "Boleto",
  onGrantLostFocus: () => {},
};

export default MobileForm;
