import React from "react";
import PropTypes from "prop-types";

import Header from "../../components/Header";
import List from "../../components/List";
import {
  Container,
  Painel,
  Button,
  Baseline,
  Label,
  Total,
  Quantity,
  PainelItems,
} from "./styles";

function View({ width }) {
  function renderInfo(justify = "baseline") {
    const price = (260.0).toLocaleString("pt-BR", {
      style: "currency",
      currency: "BRL",
    });

    const quantity = 4;

    return (
      <>
        <Baseline>
          <Quantity>{quantity}</Quantity>
          <Label justify={"baseline"}>
            {quantity === 1 ? "item" : "items"}
          </Label>
        </Baseline>
        <Baseline justify={justify}>
          <Label>total</Label>
          <Total>{price}</Total>
        </Baseline>
      </>
    );
  }

  return (
    <Container>
      <Header />
      {width < 824 && renderInfo("center")}
      <Painel>
        <PainelItems>
          <Button>fechar pedido</Button>
          {width > 824 && renderInfo()}
        </PainelItems>
      </Painel>
      <List noFilters />
    </Container>
  );
}

View.propTypes = {
  width: PropTypes.number,
};

View.defaultProps = {
  width: window.innerWidth,
};

export default View;
