import React from "react";
import PropTypes from "prop-types";

import Header from "../../components/Header";
import { Container, Painel, Button } from "./styles";

function View({ width }) {
  function renderInfo() {
    const price = (260.0).toLocaleString("pt-BR", {
      style: "currency",
      currency: "BRL",
    });

    return (
      <>
        <FormatItem>
          <Quantity>4</Quantity>
          <Label>items</Label>
        </FormatItem>
        <Total>
          <Label>total</Label>
          <Value>{price}</Value>
        </Total>
      </>
    );
  }

  return (
    <Container>
      <Header />
      <Painel>
        <Button>fechar pedido</Button>
        {width > 824 && renderInfo()}
      </Painel>
    </Container>
  );
}

View.propTypes = {
  width: PropTypes.number,
};

View.defaultProps = {
  width: window.innerWidth,
};

export default View;
