import styled from "styled-components";
import InputMask from "react-input-mask";
import { FiCreditCard } from "react-icons/fi";
import { Link } from "react-router-dom";

export const Container = styled.section.attrs({
  name: "payament",
})`
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const Column = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
`;

export const Card = styled.div`
  width: 450px;
  height: 263px;
  border-radius: 20px;
  background: #eee;
  margin-right: 50px;
`;

export const Form = styled.div`
  width: 400px;
  height: 346px;
  background: #eee;
  border-radius: 20px;
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
`;

export const WrapperInput = styled.div`
  display: flex;
  align-items: space-between;
  flex-direction: column;
  margin-top: ${(props) => (props.marginTop ? props.marginTop : 0)}px;
`;

export const SmallLabel = styled.p`
  font-size: 18px;
  margin-bottom: 10px;
`;

export const CardInput = styled(InputMask).attrs({
  mask: "0000 0000 0000 0000",
})`
  font-family: "OcrA";
  font-size: 24px;
`;

export const Row = styled.div`
  width: 300px;
  display: flex;
  /* justify-content: flex-start; */
  /* align-items: flex-start; */
  margin-top: ${(props) => (props.marginTop ? props.marginTop : 0)}px;
`;

export const CVVInput = styled(InputMask).attrs({
  mask: "000",
})`
  font-size: 24px;
  width: 100px;
`;

export const DateInput = styled(InputMask).attrs({
  mask: "00/0000",
})`
  font-size: 24px;
  width: 100px;
  background: red;
  margin-left: 50px;
`;

export const Input = styled.input`
  font-size: 24px;
  margin-top: ${(props) => (props.marginTop ? props.marginTop : 0)}px;

  &:focus {
    &::placeholder {
      color: #000;
    }
  }
`;

export const Buttons = styled.div`
  display: flex;
  justify-content: center;
  align-items: center;
  position: absolute;
  bottom: 50px;
`;

export const Button = styled.button`
  width: 150px;
  height: 55px;
  background: #424242;
  border-radius: 10px;
`;

export const CreditCard = styled(FiCreditCard)`
  width: 32px;
  height: 32px;
  color: #fff;
`;

export const Back = styled(Link)`
  font-size: 24px;
  margin-right: 50px;
  text-decoration: none;
`;
