import React, { useState, useEffect } from "react";
import PropTypes from "prop-types";

import Content from "../Content";
import {
  Container,
  Search,
  Icon,
  Input,
  WrapperMenu,
  Menu,
  Label,
} from "./styles";

function List() {
  const [nav, setNav] = useState("Todos");
  const categories = [
    "Todos",
    "Camisas",
    "Blusas",
    "Bones",
    "Casacos",
    "Meia",
    "Shorts",
    "Copos",
    "Canecas",
    "Tirantes",
  ];

  // const products = useSelector((state) => state.products.products);
  // const categories = useSelector((state) => state.products.categories);

  const products = [
    {
      id: 1,
      name: "Nome do produto",
      image: "",
      stock: 5,
      amount: 0,
    },
    {
      id: 2,
      name: "Nome do produto",
      image: "",
      stock: 5,
      amount: 0,
    },
    {
      id: 3,
      name: "Nome do produto",
      image: "",
      stock: 2,
      amount: 0,
    },
    {
      id: 4,
      name: "Nome do produto",
      image: "",
      stock: 0,
      amount: 0,
    },
  ];

  function onClick(event) {
    setNav(event.target.innerHTML);
  }

  function renderLabel(name) {
    const selected = categories.find((item) => item === nav);

    if (name === selected)
      return (
        <Label onClick={onClick} selected>
          {name}
        </Label>
      );

    return <Label onClick={onClick}>{name}</Label>;
  }

  return (
    <Container>
      <Content>
        <Search>
          <Input placeholder="Filtrar" />
          <Icon />
        </Search>
        <WrapperMenu>
          <Menu>{categories.map((name) => renderLabel(name))}</Menu>
        </WrapperMenu>
      </Content>
    </Container>
  );
}

List.propTypes = {
  data: PropTypes.array,
};

List.defaultProps = {
  data: [],
};

export default List;
