import React from "react";
import PropTypes from "prop-types";

import Content from "../../components/Content";
import Header from "../../components/Header";
import List from "../../components/List";
import {
  Container,
  Painel,
  Button,
  Baseline,
  Label,
  Total,
  Quantity,
} from "./styles";

function View({ width }) {
  function renderInfo() {
    const price = (260.0).toLocaleString("pt-BR", {
      style: "currency",
      currency: "BRL",
    });

    const quantity = 4;

    return (
      <>
        <Baseline>
          <Quantity>{quantity}</Quantity>
          <Label>{quantity === 1 ? "item" : "items"}</Label>
        </Baseline>
        <Baseline>
          <Label>total</Label>
          <Total>{price}</Total>
        </Baseline>
      </>
    );
  }

  return (
    <Container>
      <Header />
      <Painel>
        <PainelItems>
          <Button>fechar pedido</Button>
          {width > 824 && renderInfo()}
        </PainelItems>
      </Painel>
      <List noFilters />
    </Container>
  );
}

View.propTypes = {
  width: PropTypes.number,
};

View.defaultProps = {
  width: window.innerWidth,
};

export default View;
