import React, { useState, useEffect } from "react";

import {
  FacebookShareButton,
  EmailShareButton,
  WhatsappShareButton,
} from "react-share";
import {
  addToCart,
  removeFromCart,
  removeOneFromCart,
} from "../../store/modules/cart/actions.js";
import { setRoute } from "../../store/modules/route/actions";

import { useDispatch, useSelector } from "react-redux";
import { indexProductsRequest } from "~/store/modules/products/actions";

import Header from "../../components/Header";

import {
  Container,
  Name,
  Description,
  Controller,
  Minus,
  Count,
  Plus,
  Trash,
  Board,
  Photo,
  Price,
  Total,
  Label,
  Value,
  Facebook,
  Whatsapp,
  Email,
  Social,
  Tags,
  Tag,
  Content,
  Info,
} from "./styles";

function Product({ match: { params } }) {
  const { id } = params;

  const products = useSelector((state) => state.products.products);
  const product = products.find((one) => one.id === parseInt(id));
  const totalCart = useSelector((state) => state.cart.total);
  const productsCart = useSelector((state) => state.cart.products);

  const dispatch = useDispatch();

  useEffect(() => {
    window.scrollTo(0, 0);

    dispatch(setRoute("Product"));
    dispatch(indexProductsRequest());
  }, []);

  const price =
    product &&
    product.unit_price.toLocaleString("pt-BR", {
      style: "currency",
      currency: "BRL",
    });

  const total = totalCart.toLocaleString("pt-BR", {
    style: "currency",
    currency: "BRL",
  });

  let count = 0;

  productsCart.forEach((product) => {
    if (product.id === product.id) count = product.amount;
  });

  return (
    <>
      <Header />
      <Container>
        <Content>
          <Board>
            <Photo
              imageSmall={product && product.imageSmall}
              src={product && product.image}
            />
            <Price>{price}</Price>
          </Board>
          <Info>
            <Name>{product && product.name}</Name>
            <Tags>
              <Label>tags:</Label>
              <Tag>{product && product.category.category}</Tag>
            </Tags>
            <Description>{product && product.description}</Description>
            <Controller>
              <Minus onClick={() => dispatch(removeOneFromCart(product))} />
              <Count>{count}</Count>
              <Plus onClick={() => dispatch(addToCart(product))} />
              <Trash onClick={() => dispatch(removeFromCart(product))} />
            </Controller>
            <Total>
              <Label>total</Label>
              <Value>{total}</Value>
            </Total>
          </Info>
          <Social>
            <FacebookShareButton url={product && product.image}>
              <Facebook />
            </FacebookShareButton>
            <EmailShareButton url={product && product.image}>
              <Email />
            </EmailShareButton>
            <WhatsappShareButton url={product.image}>
              <Whatsapp />
            </WhatsappShareButton>
          </Social>
        </Content>
      </Container>
    </>
  );
}

export default Product;
