import styled from "styled-components";

export const Container = styled.div`
  flex: 1;
  height: ${window.innerHeight}px;
  position: relative;
  display: flex;
  align-items: center;
  flex-direction: column;
`;

export const Painel = styled.div`
  height: 85px;
  width: 100%;
  background: #fff;
  position: fixed;
  bottom: 0;
  left: 0;
  display: flex;
  align-items: center;
  justify-content: center;

  @media (min-width: 824px) {
    position: absolute;
    top: 80px;
    height: 130px;
    justify-content: flex-end;
  }
`;

export const Button = styled.button`
  padding: 5px 10px;
  border-radius: 10px;
  background: transparent;
  font-size: 18px;
  text-align: center;
  letter-spacing: 2.5px;
  border: 1px solid #000;

  @media (min-width: 824px) {
    letter-spacing: 5px;
    margin-right: 100px;
  }
`;

export const Baseline = styled.div`
  display: flex;
  align-items: baseline;
  justify-content: center;

  @media (min-width: 824px) {
    & + & {
      margin-left: 50px;
    }
  }
`;

export const Quantity = styled.p`
  font-size: 32px;
  color: #ffd271;
  margin-right: 10px;
  font-weight: bold;
`;

export const Total = styled.p`
  font-size: 32px;
  font-weight: bold;
  margin-left: 10px;
  color: #000;

  @media (min-width: 824px) {
    margin-right: 30px;
  }
`;

export const Label = styled.p`
  font-size: 18px;
`;
